import tinycolor, { random } from 'tinycolor2'

export default function useGenerateGradients(num){
   const grad =[]
    for(let i = 0; i< num;i++){
      // Generate two random colors
      // const color1 = tinycolor.random().toHexString();
      // const color2 = tinycolor.random().toHexString();
      const color1 = tinycolor.random().analogous().toString();
      const color2 = tinycolor(color1).analogous().toString();
      const directions = ["to right","to left","to bottom", "to top"]
      const direction = directions[Math.floor(Math.random()*directions.length)]
      // Create the CSS linear gradient string
      const gradient = `linear-gradient(${direction}, ${color1}, ${color2})`;

      // Return the gradient
      grad.push(gradient)
    }
  return grad   //an array of gradients
}
