import { useState } from "react";
import { FiCopy } from "react-icons/fi";
import useGenerateTintsAndShades from "./Utils/useGenerateTintsAndShades";
function App() {
  const [colorValue, setColorValue] = useState("#4db64d");
  const [copy, setCopy] = useState({index:'',state});
  function handleChange(e) {
    e.preventDefault();
    setColorValue(e.target.value);
  }
  function handleCopy(col) {
    setCopy(true);
    navigator.clipboard.writeText(col);
    setTimeout(() => setCopy(!copy), 2000);
  }
  const { tintss, shadess } = useGenerateTintsAndShades(colorValue, 15);
  console.log(tintss);
  return (
    <section>
      <h1 className="header">Tints & Shades</h1>
      <div className="color-picker">
        <input
          type="text"
          className="color-text "
          onChange={handleChange}
          value={colorValue}
        />
        <div className="cl" style={{ backgroundColor: colorValue }}>
          <input
            type="color"
            name="color-input"
            className="color-input"
            onChange={handleChange}
            onInput={handleChange}
          />
        </div>
      </div>
      <div className="picked" style={{ backgroundColor: colorValue }}>
        {colorValue}
      </div>

      {/* //map over the tints and shades */}

      <div className="wr">
        Shades - (Darken)
        <div className="display shades" style={{ borderColor: colorValue }}>
          {shadess?.map((col, index) => {
            return (
              <button
                className="hue-color"
                key={"S" + index}
                style={{ backgroundColor: col }}
                onClick={(e) => {
                  e.preventDefault();
                  handleCopy(col);
                }}
              >
                {copy.state && copy.index === 'S'+index ? (
                  <div className="copied">
                    <FiCopy />
                    <p>copied</p>{" "}
                  </div>
                ) : (
                  col
                )}
              </button>
            );
          })}
        </div>
      </div>
      <div className="wr">
        Tints - (Lighten)
        <div className="display shades" style={{ borderColor: colorValue }}>
          {tintss?.map((col, index) => {
            return (
              <button
                className="hue-color"
                key={"T" + index}
                style={{ backgroundColor: col }}
                onClick={(e) => {
                  e.preventDefault();
                  handleCopy(col);
                }}
              >
                {copy.state && copy.index === 'S'+index ? (
                  <div className="copied">
                    <FiCopy />
                    <p>copied</p>{" "}
                  </div>
                ) : (
                  col
                )}
              </button>
            );
          })}
        </div>
      </div>
    </section>
  );
}

export default App;
