import { useState } from "react";

function App() {
  const [colorValue, setColorValue] = useState("#4db64d");

  function handleChange(e) {
    e.preventDefault();
    setColorValue(e.target.value);
  }

 const {tintss,shades} = useGenerateTintsAndShades(colorValue,15)

  return (
    <section>
      <h1 class="header">Tints & Shades</h1>
      <div class="color-picker">
        <input
          type="text"
          class="color-text "
          onChange={handleChange}
          value={colorValue}
        />
        <div class="cl" style={{ backgroundColor: colorValue }}>
          <input
            type="color"
            name="color-input"
            class="color-input"
            onChange={handleChange}
            onInput={handleChange}
          />
        </div>
      </div>
      <div class="picked" style={{ backgroundColor: colorValue }}>{colorValue}</div>

      {/* //map over the tints and shades */}
      tints
      <div class="wr">
        Shades - (Darken)
        <div class="display shades"></div>
      </div>
      <div class="wr">
        Tints - (Lighten)
        <div class="display tints"></div>
      </div>
    </section>
  );
}

export default App;
