import { useRef, useState } from "react";
import { FiCopy, FiGithub,FiRefreshCcw } from "react-icons/fi";


import useGenerateGradients from "./Utils/useGenerateGradients";
function App() {
  const copy = useRef({ index: "", state: false });

  function handleCopy(col, index) {
    copy.current = { index: index, state: true }
    navigator.clipboard.writeText(col);
    console.log(col)
    console.log(copy.current)
    setTimeout(() => copy.current = { index: "", state: false }, 2000);
  }

   const handleReload =()=>window.location.reload()

  const grad = useGenerateGradients(30);
  return (
    <section>
      <h1 className="header">Gradientss</h1>

      {/* //map over the gradients*/}
      <div className="display">
        {grad.map((gradient, index) => {
          return (
            <button
              key={"grad" + index}
              style={{ background: gradient }}
              className="hue-color"
              onClick={(e) => {
                e.preventDefault()
                handleCopy(gradient,'grad'+index);
              }}
            >
              {copy.current.state === true && copy.current.index === "grad" + index ? (
                <div className="copied">
                  <FiCopy />
                  <p>copied</p>{" "}
                </div>
              ) : (
                <div className="copied">
                  <FiCopy />
                  <p>click to copy</p>{" "}
                </div>
              )}
            </button>
          );
        })}
      </div>


      <div className="git-repo">
        <a href="https://github.com/Newton-Nganga/tints-and-shades.git">
          <FiGithub />
          Github
        </a>
      </div>
      <div className="brandy">
        <span>ðŸ¥³</span> by Newton
      </div>
      <button className="refresh" onClick={handleReload}>
         <FiRefreshCcw/>
      </button>
    </section>
  );
}

export default App;
